		string[] puzzles = sample.Split('\n');

		int puzzleNum = (int)Mathf.Floor(Random.Range(0, 99.9f));
		//string[] lines = File.ReadAllLines (path);
		//string line = lines [puzzleNum];
		string line = puzzles[puzzleNum];

		GameManager.Puzzle puzzle = new GameManager.Puzzle(new int[9,9], new int[9,9]);

		for (int i = 0; i < 81; i++) {
			if (line [i] == '.') {
				puzzle.unsolved [i / 9, i % 9] = 0;
				//MonoBehaviour.print (puzzle.unsolved [i / 9, i % 9]);
			}
			else {
				int temp;
				int.TryParse (line [i].ToString (), out temp);
				puzzle.unsolved [i / 9, i % 9] = temp;
				//MonoBehaviour.print (puzzle.unsolved [i / 9, i % 9]);
			}
		}

		for (int i = 82; i < 163; i++) {
			int temp;
			int.TryParse (line [i].ToString (), out temp);
			puzzle.solved [(i - 82) / 9, (i - 82) % 9] = temp;
			//MonoBehaviour.print (puzzle.solved [(i - 82) / 9, (i - 82) % 9]);

		}

		return puzzle;
